import React from 'react';
import DeleteButton from '../buttons/DeleteButton';
import { Link } from 'react-router-dom';
import EditButton from '../buttons/EditButton';


// import CardList from '../../../../Flashcard-o-matic/src/components/cards/CardList';

function Deck({ deck, currentPage }) {
    const { cards } = deck;

    return (
        <div className="container" style={{border: "black"}}>
            <div className="container">
                <h3>{deck.name}</h3>
                <p>{cards.length} Cards</p>
            </div>
            <p>{deck.description}</p>
            <div className="container">
                <div className="row">
                    <div className="col-2">
                    {currentPage === "DeckList" && (
                        <div >
                            <Link to={`/decks/${deck.id}`} className="btn btn-secondary">View</Link>
                        </div>
                    )}
                    </div>
                    <div className="row">
                    <div className="col-2">
                        { currentPage === "ViewDeck" && (
                            <EditButton
                            routePath={`/decks/${deck.id}/edit`}
                            buttonFunction={"Deck"}
                            />
                        )

                        }
                        </div>
                        <div className="col-2">
                            <button className="btn btn-secondary">Study</button>
                        </div>
                        <div className="col-2">
                            <button className="btn btn-secondary">Add Cards</button>
                        </div>
                        <div className="col-6">
                            <DeleteButton id={deck.id} currentPage={currentPage} buttonFunction={"Deck"} />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )


}

export default Deck;
