import React from 'react';
import DeleteButton from '../buttons/DeleteButton';


// import CardList from '../../../../Flashcard-o-matic/src/components/cards/CardList';

function Deck({ deck, currentPage }) {
    const { name, description, cards, id } = deck;

    return (
        <div className="container">
            <div className="container">
                <h3>{deck.name}</h3>
                <p>{cards.length} Cards</p>
            </div>
            <p>{deck.description}</p>
            <div className="container">
                <div className="row">
                    <div className="col-2">
                        <button>View</button>
                    </div>
                    <div className="col-2">
                        <button>Study</button>
                    </div>
                    <div className="col-8">
                        <DeleteButton id={deck.id} deck={deck} currentPage={currentPage} />

                    </div>
                </div>
            </div>
        </div>
    )


}

export default Deck;
